import React, { useState } from "react";

interface ListProps {
  users: {
    id: string;
    username: string;
    email: string;
    gender: string;
    age: number;
    first_name: string;
    last_name: string;
    phone_number: string;
    created_at: string;
    is_active: boolean;
  }[];
}

const List: React.FC<ListProps> = ({ users }) => {
  const [selectedUser, setSelectedUser] = useState<any | null>(null);

  // Function to open the modal with user data
  const handleViewClick = (user: any) => {
    setSelectedUser(user);
  };

  // Function to close the modal
  const handleCloseModal = () => {
    setSelectedUser(null);
  };

  return (
    <div className="p-4">
      <h1 className="text-xl font-semibold mb-4">User List</h1>
      <ul className="divide-y divide-gray-200">
        {users.map((user) => (
          <li key={user.id} className="py-4 flex items-center justify-between">
            <div className="flex items-center">
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-900">{user.username}</p>
                <p className="text-sm text-gray-500">{user.email}</p>
              </div>
            </div>
            <button
              className="text-sm font-medium text-indigo-600 hover:text-indigo-500"
              onClick={() => handleViewClick(user)}
            >
              View
            </button>
          </li>
        ))}
      </ul>

      {/* Modal to show additional user details */}
      {selectedUser && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
          <div className="bg-white rounded-lg w-96 p-6 shadow-lg">
            <div className="flex justify-between items-center">
              <h3 className="text-lg font-semibold">{selectedUser.first_name} {selectedUser.last_name}</h3>
              <button
                onClick={handleCloseModal}
                className="text-sm font-medium text-gray-600 hover:text-gray-800"
              >
                Close
              </button>
            </div>

            <div className="mt-4">
              <p><strong>Email:</strong> {selectedUser.email}</p>
              <p><strong>Gender:</strong> {selectedUser.gender}</p>
              <p><strong>Age:</strong> {selectedUser.age}</p>
              <p><strong>Phone Number:</strong> {selectedUser.phone_number}</p>
              <p><strong>Created At:</strong> {new Date(selectedUser.created_at).toLocaleDateString()}</p>
              <p><strong>Status:</strong> {selectedUser.is_active ? "Active" : "Inactive"}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default List;
